INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (1, 'Трахісан', 'Препарат Трахісан містить комбінацію речовин, що володіють антибактеріальними властивостями та місцевоанестезуючу дію.', 1);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (2, 'Синупрет', 'Препарат Синупрет в лікуванні і профілактиці ускладнень гострої респіраторної інфекції у дітей та дорослих.', 2);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (3, 'Амброксол', 'Препарат Амброксол створює сприятливі умови для відокремлення та виведення слизу з дихальних шляхів, а також зменшення виразності кашлю.', 3);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (4, 'Мукалтин', 'Препарат Мукалтин має помірну протикашльову дію, зменшує кількість нападів кашлю та їх інтенсивність.', 4);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (5, 'Мукалтин Форте з вітаміном С', 'Препарат Мукалтин Форте з Вітаміном С - відхаркувальний засіб, дія якого обумовлена секретолітичними та бронхолітичними властивостями.', 5);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (6, 'Півонії настойка', 'Настоянка півонії підвищує опір організму до гіпоксії, має спазмолітичні, протисудомні, антимікробні властивості та здатність підвищувати кислотність шлунка.', 6);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (7, 'Корвалол-Дарниця', 'Корвалол-Дарниця виявляє седативну і спазмолітичну дію, яке обумовлюється його складовими.', 7);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (8, 'Парацетамол-Дарниця', 'Парацетамол-Дарниця ― лікарський препарат, що має жарознижуювальні та анальгетичні властивості.', 7);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (9, 'Гіоксизон', 'Мазь Гіоксизон є комплексним препаратом. Поєднує антибактеріальну дію окситетрацикліну з протизапальним, антиалергічним, анти ексудативним ефектом гідрокортизону', 8);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (10, 'Фастум гель 2.5%', 'Фастум гель чинить протизапальну та аналгетичну дію.', 9);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (11, 'Диклофенак-Здоров`я 1%', 'Диклофенак-Здоров`я протизапальну та знеболювальну дію', 10);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (12, 'Диклофенак', 'Диклофенак - нестероїдний протизапальний та протиревматичний засіб.', 8);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (13, 'КЛІЩ-OUT', 'Захист від кліщів та інших двокрилих і павукоподібних комах', 11);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (14, 'Контроль пітливості "HAPPY FEET"', 'Аерозоль для ніг контроль пітливості "HAPPY FEET" сприяє профілактиці надмірного потовиділення ніг. ', 11);
INSERT INTO pharmacy.product (id, name, description, manufacturer_id) VALUES (15, 'Бальзам для стоп БІОКОН', 'Бальзам для грубої потрісканої шкіри стоп Доктор Біокон розм`якшує і м`яко відлущує огрубілу шкіру стоп.', 12);